import languageExtensions from"./assets/languageExtensions.json"assert{type:"json"};export default function getProgrammingLanguage(fileName){if(fileName.charAt(0)==="."){return languageExtensions[fileName]}let fileExtensionArray=fileName.split(".");let fileExtension;if(fileExtensionArray.length>1){fileExtensionArray.shift();fileExtension=fileExtensionArray.join(".");fileExtension="."+fileExtension}else{fileExtension="."+fileExtensionArray[0]}return languageExtensions[fileExtension]}